mapper.select:
SELECT a.id, a.user_id, a.category_id, a.title, a.summary, a.content, a.cover, a.tags, a.view_count, a.is_pinned, a.is_published, a.created_at, a.updated_at, c.name AS category_name, COALESCE(u.nickname, u.username) AS author_name,
CASE WHEN f.id IS NOT NULL THEN 1 ELSE 0 END AS isExist
0 AS isExist
FROM article a LEFT JOIN category c ON a.category_id = c.id LEFT JOIN user u ON a.user_id = u.id
LEFT JOIN favorite f ON a.id = f.article_id AND f.user_id = #{currentUserId}
WHERE a.is_delete = 0
AND a.id = #{id}
AND a.user_id = #{userId}
AND a.category_id = #{categoryId}
AND a.is_pinned = #{isPinned}
AND a.is_published = #{isPublished}
AND a.title LIKE CONCAT('%', #{keyword}, '%')
ORDER BY a.is_pinned DESC, a.created_at DESC
